(()=>{var u=["ubuntu","alpine"];u.forEach(e=>{let t=document.querySelector(`#${e}-modal`),d=document.querySelector(`.open-dialog-${e}`),n=document.querySelector(`.close-dialog-${e}`),r=document.querySelector(`.copy-to-clipboard-${e}`),c=document.querySelector(`.code-${e}`),l=document.querySelector(`.copied-${e}`);r.addEventListener("click",o=>{c.innerHTML&&(o.preventDefault(),navigator.clipboard.writeText(c.innerHTML),l.style.setProperty("display","block"))}),d.addEventListener("click",o=>{o.preventDefault(),t.showModal()}),n.addEventListener("click",o=>{o.preventDefault(),t.close(),l.style.setProperty("display","none")})});})();
